CANCELLED_ERROR_CODES = {
    0x0300400C,
    0x0500400E,
}

FILAMENT_RUNOUT_ERROR_CODES = {
    0x03008004,
    0x03008015,
    0x07FF8011,
    0x12FF8011,
    0x12018011,
    0x12028011,
    0x12038011,
    0x07008011,
    0x07018011,
    0x07028011,
    0x07038011,
    0x12008011,
}


def parse_error_code(error_code: int | None) -> str:
    if error_code:
        return ERROR_CODES.get(error_code, f"Unknown error code {hex(error_code)}")
    return "Unknown error code"
